mapper.update:
update tbl_board set title =#{title}, content
=#{content}
where BNO=#{bno}

mapper.select:
select * from
(select rownum rn, bno, title, content, writer, regdate, viewcnt, replycnt from TBL_BOARD where bno>0 order by bno desc)
where rn between #{pageStart} and #{perPageNum}


update tbl_board set viewcnt =
 viewcnt + 1 
 where bno = #{bno}
